var steps = 1000;
var path =
  '';

function countingValleys(steps, path) {
  var seaLevel = 0;
  var counter = 0;
  for (let i = 0; i < steps; i++) {
    if ((path[i] == 'D') & (seaLevel == 0)) {
      seaLevel--;
      counter++;
      console.log(
        `entrando no vale pela ${counter} vez, sea level Ã© = a ${seaLevel}`
      );
    } else if ((path[i] == 'D') & (seaLevel != 0)) {
      seaLevel--;
      console.log(seaLevel);
    } else if (path[i] == 'U') {
      seaLevel++;
      console.log(seaLevel);
    }
  }
  return counter;
}

console.log(countingValleys(steps, path));

